import axios from 'axios'

const instance = axios.create({
	baseURL: 'https://jsonplaceholder.typicode.com/',
})
export const usersAPI = {
	getUsers: async () => {
		const response = await instance
			.get(`posts/`)
		return response.data
	},
	getProfileData(userId) {
		console.warn('Obsolete method.Please profileAPI object.')
		return profileAPI.getProfileData(userId)
	},
	getCommentsData(userId) {
		console.warn('Obsolete method.Please profileAPI object.')
		return commentsAPI.getCommentsData(userId)
	},
}
export const profileAPI = {
	getProfileData(userId) {
		return instance.get('posts/' + userId).then(response => {
			return response.data
		})
	},

	updateStatus(status) {
		return instance.put('profile/status', { status: status }).then(response => {
			return response.data
		})
	},

	getStatus(userId) {
		return instance.get('profile/status/' + userId).then(response => {
			return response.data
		})
	},
}

export const commentsAPI = {
	getCommentsData(userId) {
		return instance.get(`posts/${userId}/comments`).then(response => {
			return response.data
		})
	},
}
