import React from 'react';
import s from './Users.module.css';
import userDefaultAvatar from '../../assets/images/default-avatar-icon.png'


let Users = (props)=>{

	let FollowingHandle = userId => {
		if (props.user.followed) {
			props.onUnfollow(userId)
		} else {
			props.onFollow(userId)
		}
	}

	return (
		<div className={s.userItem}>
			<div>
				<NavLink to={'/posts/' + props.user.id}>
					<img className={s.avatar} src={props.defaultAvatar} alt='' />
				</NavLink>
			</div>
			<div>{props.user.id} </div>
			<div>{props.user.title} </div>
			<div>{props.user.body} </div>
			<div>
				<NavLink to={'/posts/'+props.user.id+'/commets'}>
					<button>Comments</button>
				</NavLink>
			</div>
		</div>
	)
}

export default Users;
